mapper.select:
select

from JBF_REQUIRE_INFO t
WHERE 1=1
AND REQUEST_STATUS='A'

  AND t.REQUIRE_CUST  = #{requireCust,jdbcType=VARCHAR}


  AND t.PROJECT =  #{project,jdbcType=VARCHAR}


  AND t.POSITIONS = #{position,jdbcType=VARCHAR}


  AND t.PRIORITY = #{priority,jdbcType=VARCHAR}

mapper.update:
update JBF_REQUIRE_INFO set
STATUS = #{status,jdbcType=VARCHAR},
where NUM_NO = #{numNo,jdbcType=VARCHAR}
