mapper.select:
select ID,MENU_ID,MENU_NAME,MENU_LEVEL,MENU_LEVEL_DESC,
MENU_LEVEL_PARENT,PATH,BACK_UP,STATUS,
(select b.Menu_name from JBF_MENU b
where b.MENU_ID=j.MENU_LEVEL_PARENT) as MENU_PARENT_NAME
from JBF_MENU j


    MENU_ID=#{MENU_ID}


    and MENU_NAME=#{MENU_NAME}


     and MENU_LEVEL_PARENT=#{MENU_LEVEL_PARENT}


        and status=#{status}

select m.ID,m.MENU_ID,m.MENU_NAME, m.MENU_LEVEL_PARENT  from JBF_MENU m


        m.MENU_LEVEL=#{MENU_LEVEL}


        and  m.menu_id=#{menu_id}


order by m.MENU_LEVEL asc

SELECT d.*, c.MENU_NAME as Menu_parent_name from ( SELECT b.MENU_ID , b.MENU_NAME , b.MENU_LEVEL, b.MENU_LEVEL_PARENT, b.path as patch, 'false' as active, 'false' as grade FROM JBF_MENU b where b.MENU_LEVEL=#{menu_level}+1 and b.menu_id in
#{item}
and b.MENU_LEVEL_PARENT in ( select a.MENU_ID from JBF_MENU a WHERE a.MENU_LEVEL =#{menu_level} ) ) d left join ( SELECT t.MENU_ID, t.MENU_NAME, t.MENU_LEVEL, t.MENU_LEVEL_PARENT FROM JBF_MENU t WHERE t.MENU_LEVEL =#{menu_level} ORDER BY t.MENU_ID asc ) c on c.MENU_ID =d.MENU_LEVEL_PARENT
